/* Box sizing rules */
*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
  margin: 0;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
  list-style: none;
}

/* Set core root defaults */
html:focus-within {
  scroll-behavior: smooth;
}

/* Set core body defaults */
body {
  min-height: 100vh;
  text-rendering: optimizeSpeed;
  line-height: 1.5;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
  text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
  max-width: 100%;
  display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
  font: inherit;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
   scroll-behavior: auto;
  }
  
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

// CSS Reset end

// CSS variables
:root {

    // background:
    --darker-blue-bg: #0F1D2A;
    --ligher-blue-bg: #1E3554;
    --darker-green-bg: #001510;
    --ligher-green-bg: #11283A;
    --light-accent-bg: #E9997F; // rgb(233, 153, 127)

    // font color:
    --black: black;
    --white: white;
    --heading-text-shadow: 0px 0px 2vmax #E9997F;

    // font size:
    --main-header-size: calc(12px + 4vmax);
    --main-header-size-mobile: calc(12px + 3vmax);
    --main-subheader-size: calc(12px + 2vmax);
    --paragraph-size: 2rem;

    // font family
    --font-main-heading: 'Sigmar One', cursive;
    --font-sub-heading: 'Hammersmith One', sans-serif;

}

///////////////////////////////////////
// SECTION 1 -- SECTION 1 -- SECTION 1
///////////////////////////////////////

.section1 {
    width: 100%;
    height: 100%;
    display: flex;
    .imageTextContainer {
        width: 100%;
        height: 100%;
        .heroText {
          position: absolute;
          z-index: 1;
          width: 100%;
          height: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
          top: 20%;
          h1, h2 {
            transform: translateY(50px);
            transition: 0.6s ease-in-out;
            clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%);
            text-align: center;
            padding: 0em 2rem;
            text-shadow: var(--heading-text-shadow);
          }
          h1 {
            font-family: var(--font-main-heading);
            font-size: var(--main-header-size);
            @media screen and (max-width: 300px) {
              & {
                font-size: var(--main-header-size-mobile);
              }
            }
          }
          h2 {
            font-family: var(--font-sub-heading);
            font-size: var(--main-subheader-size);
          }
        }
        .heroImages {
            width: 100%;
            height: 100vh;
            display: flex;
            position: relative;
            overflow: hidden;
            .heroImg {
                position: absolute;
                object-fit: cover;
                height: 100%;
                width: 100%;
                bottom: 0;
                object-position: bottom;
            }
            .heroImg1 {
                transform: translateY(160px);
                z-index: 3;
                @media screen and (max-width: 1600px) {
                    & {
                        transform: translateY(90px);
                    }
                }
            }
            .heroImg2 {
                transform: translateY(50px);
                z-index: 2;
                @media screen and (max-width: 1600px) {
                    & {
                        transform: translateY(20px);
                    }
                }
            }
        }
    }
}

///////////////////////////////////////
// SECTION 2 -- SECTION 2 -- SECTION 2
///////////////////////////////////////

.section2 {
    content: '';
    height: 100vh;
    width: 100%;
    background-color: var(--darker-blue-bg);
    z-index: 1;
}
