  overflow: hidden;
  @media screen and (max-width: 900px) {
    & {
      min-height: 40vh;
    }
  }
  .railroadContainer {
    width: 100%;
    overflow: hidden;
    padding: 2rem 0rem 0rem 0rem;
    transform: rotate(1.5deg) scale(1.01);
    position: relative;
    .railroadTrack {
      min-height: 200px;
      width: 100%;
      object-fit: cover;
    }
    .railroadTrackDesktop {
      display: block;
    }
    .railroadTrackMobile {
      display: none;
    }
    @media screen and (max-width: 700px) {
      .railroadTrackDesktop {
        display: none;
      }
      .railroadTrackMobile {
        display: block;
      }
    }
    .htmlCart {
      position: absolute;
      bottom: 0%;
      height: 19vmax;
      animation: moveCart 20s infinite linear;
    }
    .htmlCartWheel {
      animation: cartWheel 20s infinite linear;
    }
    .cssCart {
      position: absolute;
      bottom: 0%;
      left: -20%;
      height: 19vmax;
      animation: moveCart 20s infinite linear;
      animation-delay: 3s;
    }
    .cssCartWheel {
      animation: cartWheel 20s infinite linear;
      animation-delay: 3s;
      left: -20%;
    }
    .jsCart {
      position: absolute;
      bottom: 0%;
      left: -20%;
      height: 19vmax;
      animation: moveCart 20s infinite linear;
      animation-delay: 6s;
    }
    .jsCartWheel {
      animation: cartWheel 20s infinite linear;
      animation-delay: 6s;
      left: -20%;
    }
    .nodeCart {
      position: absolute;
      bottom: 0%;
      left: -20%;
      height: 19vmax;
      animation: moveCart 20s infinite linear;
      animation-delay: 9s;
    }
    .nodeCartWheel {
      animation: cartWheel 20s infinite linear;
      animation-delay: 9s;
      left: -20%;
    }
    .vueCart {
      position: absolute;
      bottom: 0%;
      left: -20%;
      height: 19vmax;
      animation: moveCart 20s infinite linear;
      animation-delay: 12s;
    }
    .vueCartWheel {
      animation: cartWheel 20s infinite linear;
      animation-delay: 12s;
      left: -20%;
    }
    .herokuCart {
      position: absolute;
      bottom: 0%;
      left: -20%;
      height: 19vmax;
      animation: moveCart 20s infinite linear;
      animation-delay: 15s;
    }
    .herokuCartWheel {
      animation: cartWheel 20s infinite linear;
      animation-delay: 15s;
      left: -20%;
    }
    @keyframes moveCart {
      0% {
        left: -20%;
      }
      100% {
        left: 120%;
      }
    }
    .leftWheel {
      transform-origin: 32% 85%;
    }
    .rightWheel {
      transform-origin: 67% 85%;
    }
    @keyframes cartWheel {
      0% {
        transform: rotate(0deg);
        left: -20%;
      } 
      100% {
        transform: rotate(5000deg);
        left: 120%;
      }
    }
  }
}

.sectionFour {
  width: 100%;
  background-color: var(--darker-blue-bg);
  .project {
    width: 100%;
    display: flex;
    flex-direction: row;
    &:nth-child(even) {
      flex-direction: row-reverse;
    }
    @media screen and (max-width: 1000px) {
      flex-direction: column;
      &:nth-child(even) {
        flex-direction: column;
      }
    }
    .sectionFourImg {
      width: 100%;
      padding: 1rem;
      div {
        width: 100%;
        height: 100%;
        position: relative;
        overflow: hidden;
        .projectCover {
          width: 100%;
        }
        .overlay {
          position: absolute;
          top: 0;
          transition: 0.5s ease-in;
          transform-style: preserve-3d;
        }
        .bones {
          transform: perspective(500px) translateZ(20px) rotateY(0deg);
        }
        .projectImgOne {
          transform: perspective(500px) translateZ(20px) rotateY(-90deg);
        }
        .projectImgTwo {
          transform: perspective(1000px) translateZ(20px) rotateY(-90deg);
        }
        .projectVideo {
          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          transition: 0.7s ease-in;
          opacity: 0;
          .videoElement {
            transition: 1s ease-out;
            border-radius: 150px;
            width: 100%;
            height: 100%;
            object-fit: contain;
            transform: scale(0.75);
          }
        }
      }

      
      // Project 1
      .viewPerformante:hover .projectImg {
        opacity: 0;
      }
      .viewPerformante:hover .projectOneVideoEl {
        opacity: 1;
      }

      // Project 2
      .viewTwitter:hover .projectImg {
        opacity: 0;
      }
      .viewTwitter:hover .projectTwoVideoEl {
        opacity: 1;
      }

      // Project 3
      .viewVmm:hover .projectImg {
        opacity: 0;
      }
      .viewVmm:hover .projectThreeVideoEl {
        opacity: 1;
      }

      // Project 4
      .viewEmpire:hover .projectImg {
        opacity: 0;
      }
      .viewEmpire:hover .projectFourVideoEl {
        opacity: 1;
      }

      
    }
    .sectionFourText {
      width: 100%;
      padding: 1rem;
    }
  }
  .random {
    content: '';
    height: 100vh;
    width: 100%;
  }
}
